#include &lt;stdio.h&gt;
void judge(long int number);

int main()
{
    // 写入卡号n
    long int n = get_long("Number: ");

    // 判断卡号是否合法,合法时发卡机构
    judge(n);
}


void judge(long int number)
{
    // 计算Luhn数
    long int keep = number;
    long int card = number;
    int count = 0;
    while(number / 10 != 0)
    {
        int k = number / 10 % 10;
        if((0 &lt;= k * 2) &amp;&amp; (k*2&lt;=9))
        {
            count = count + 2 * k;
        }
        else
        {
            count = count + (2 * k) % 10 + (2 * k) / 10;
        }
        number = number / 100;
    }
    while(keep != 0)
    {
        count = count + keep % 10;
        keep = keep / 100;
    }
    // 提取卡首位
    int m;
    while(card != 0)
    {
        m = card;
        card = card / 10;
    }

    // 判断合法性及银行
    if(count % 10 != 0)
    {
        printf("INVALID\n");
    }
    else if(m == 4)
    {
        printf("VISA\n");
    }
    else if(m == 3)
    {
        printf("AMEX\n");
    }
    else if(m == 5 )
    {
        printf("MASTERCARD\n");
    }
}
</code></pre>

half.c<pre><code>// Calculate your half of a restaurant bill
// Data types, operations, type casting, return value

#include &lt;cs50.h&gt;
#include &lt;stdio.h&gt;

float half(float bill, float tax, int tip);

int main(void)
{
    float bill_amount = get_float("Bill before tax and tip: ");
    float tax_percent = get_float("Sale Tax Percent: ");
    int tip_percent = get_int("Tip percent: ");

    printf("You will owe $%.2f each!\n", half(bill_amount, tax_percent, tip_percent));
}

// TODO: Complete the function
float half(float bill, float tax, int tip)
{
    float n = (bill * (1.00 + tax/100.00) * (1.00 + (float)tip / 100.00)) / 2.00;
    return n;
}
</code></pre> prime.c<pre><code>#include &lt;cs50.h&gt;
#include &lt;stdio.h&gt;

bool prime(int number);

int main(void)
{
    int min;
    do
    {
        min = get_int("Minimum: ");
    }
    while (min &lt; 1);

    int max;
    do
    {
        max = get_int("Maximum: ");
    }
    while (min &gt;= max);

    for (int i = min; i &lt;= max; i++)
    {
        if (prime(i))
        {
            printf("%i\n", i);
        }
    }
}

bool prime(int number)
{
    // TODO
    int flag = 0;

    for(int x = 2;x &lt; number;x++)
    {
        if(number % x == 0)
        {
            flag = 1;
        }
    }

    if(flag == 1 || number == 1)
    {
        return (false);
    }
    else
    {
        return (true);
    }
}
